mapper.select:
SELECT company_id, acct_name, acct_code, sum( bgn_amt ) as bgn_amt, sum( dr_amt ) as dr_amt, sum( cr_amt ) as cr_amt, sum( bgn_qty ) as bgn_qty, sum( dr_qty ) as dr_qty, sum( cr_qty ) as cr_qty FROM (
SELECT company_id AS company_id , acct_name AS acct_name, acct_code AS acct_code, bgn_amt AS bgn_amt, 0 AS dr_amt, 0 AS cr_amt, bgn_qty AS bgn_qty, 0 AS dr_qty, 0 AS cr_qty FROM finance_aa_ledgerl_cal WHERE acct_period between #{startDate} and #{startDate} and acct_code between #{acctCodeStart} and #{acctCodeEnd} and company_id = #{companyId} group by acct_name , company_id,acct_code
UNION SELECT company_id AS company_id , acct_name AS acct_name, acct_code AS acct_code, 0 AS bgn_amt, sum(dr_amt) AS dr_amt, sum(cr_amt) AS cr_amt, 0 AS bgn_qty, sum(dr_qty) AS dr_qty, sum(cr_qty) AS cr_qty FROM finance_aa_ledgerl_cal WHERE acct_period between #{startDate} and #{startDate} and acct_code between #{acctCodeStart} and #{acctCodeEnd}
and (cal_namea between #{calNameaStart} and #{calNameaEnd} or cal_nameb between #{calNameaStart} and #{calNameaEnd} or cal_namec between #{calNameaStart} and #{calNameaEnd} or cal_named between #{calNameaStart} and #{calNameaEnd} )
and company_id = #{companyId} group by acct_name , company_id,acct_code
UNION select company_id,acct_name,acct_code,sum(bgn_amt) as bgn_amt,sum(dr_amt) as dr_amt, sum(cr_amt) as cr_amt,sum(bgn_qty) as bgn_qty,sum(dr_qty) as dr_qty ,sum(cr_qty) as cr_qty from ( SELECT b.company_id AS company_id, b.acct_name AS acct_name, b.acct_code AS acct_code, 0 AS bgn_amt, CASE WHEN DRCR = 'D' THEN b.ntAmt ELSE 0 END AS dr_amt, CASE WHEN DRCR = 'C' THEN b.ntAmt ELSE 0 END AS cr_amt, 0 AS bgn_qty, CASE WHEN DRCR = 'D' THEN b.qty_frnAmt ELSE 0 END AS dr_qty, CASE WHEN DRCR = 'C' THEN b.qty_frnAmt ELSE 0 END AS cr_qty FROM finance_aa_voucher a LEFT JOIN finance_aa_voucher_detail b ON b.voucher_no = a.voucher_no AND b.company_id = a.company_id WHERE b.voucher_date between #{startDate} and #{endDate} and acct_code between #{acctCodeStart} and #{acctCodeEnd}
and (cal_namea between #{calNameaStart} and #{calNameaEnd} or cal_nameb between #{calNameaStart} and #{calNameaEnd} or cal_namec between #{calNameaStart} and #{calNameaEnd} or cal_named between #{calNameaStart} and #{calNameaEnd} )
and b.company_id = #{companyId} and a.status != 'P' ) t2 group by acct_name , company_id,acct_code
) T1 GROUP BY acct_name

SELECT
        acct_code,
        acct_name
            from
        finance_aa_ledgerl_cal

             and company_id = #{companyId}
             and acct_period between #{startDate} and #{endDate}
             and acct_code between #{acctCodeStart} and #{acctCodeEnd}
            and cal_codea between #{calNameaStart} and #{calNameaEnd}

        ORDER  BY
        acct_code

SELECT company_id,acct_period,acct_code,acct_name,IFNULL(bgn_amt,0.00) as bgn_amt,IFNULL(dr_amt,0.00) as dr_amt,IFNULL(cr_amt,0.00) as cr_amt,IFNULL(bgn_qty,0.00) as bgn_qty,IFNULL(dr_qty,0.00) as dr_qty,IFNULL(cr_qty,0.00) as cr_qty,year_dr_amt,year_cr_amt,year_dr_qty,year_cr_Qty,
cal_namea as calName,
 cal_nameb ,
 cal_namec ,
 cal_named
FROM finance_aa_ledgerl_cal


     and company_id = #{companyId}
     and acct_period between #{startDate} and #{endDate}
     and acct_code = #{acctCode}
     and cal_codea between #{calNameaStart} and #{calNameaEnd}

ORDER  BY
acct_period

SELECT company_id,acct_period,acct_code,acct_name,IFNULL(bgn_amt,0.00) as bgn_amt,IFNULL(dr_amt,0.00) as dr_amt,IFNULL(cr_amt,0.00) as cr_amt,IFNULL(bgn_qty,0.00) as bgn_qty,IFNULL(dr_qty,0.00) as dr_qty,IFNULL(cr_qty,0.00) as cr_qty,year_dr_amt,year_cr_amt,year_dr_qty,year_cr_Qty,
cal_namea as calName
FROM finance_aa_ledgerl_cal


     and company_id = #{companyId}
     and acct_period between #{startDate} and #{endDate}
     and cal_namea = #{calName}

ORDER BY
acct_period

mapper.update:
update finance_aa_ledger_acct
company_id = #{companyId}, acct_period = #{acctPeriod}, acct_id = #{acctId}, acct_code= #{acctCode}, acct_name = #{acctName}, bgn_amt = #{bgnAmt}, dr_amt = #{drAmt}, cr_amt = #{crAmt}, bgn_qty = #{bgnQty}, dr_qty = #{drQty}, cr_qty = #{crQty}, year_dr_amt = #{yearDrAmt}, year_cr_amt = #{yearCrAmt}, year_dr_qty = #{yearDrQty}, year_cr_Qty = #{yearCrQty},
where id = #{id}
