mapper.update:
UPDATE finance_ft_change
company_id = #{companyId}, change_no = #{changeNo}, change_name = #{changeName}, change_type = #{changeType}, change_way = #{changeWay}, bill_nature = #{billNature}, change_date = #{changeDate}, change_desc = #{changeDesc}, asset_type = #{assetType}, apply_dept = #{applyDept}, new_asset_user = #{newAssetUser}, apply_user = #{applyUser}, apply_date = #{applyDate}, status = #{status}, period = #{period}, confirm_user = #{confirmUser}, confirm_date = #{confirmDate}, check_user = #{checkUser}, check_date = #{checkDate}, create_by = #{createBy}, create_name = #{createName}, create_time = #{createTime}, update_by = #{updateBy}, update_name = #{updateName}, update_time = #{updateTime},
WHERE uuid = #{uuid}

mapper.select:
select IFNULL(max(change_no),"")changeNo from finance_ft_change

     and company_id = #{companyId}
     and change_no like concat(#{changeNo},'%' )
