mapper.select:
select id, salary_project_id, com_pro, com_mon, per_pro, per_mon, base_ucl, base_ll, deci_bit, carry_mode, pay_area_id, effect_date, version, creator, creator_id, create_date from human_hs_social_security

    and effect_date = #{date} 
    and pay_area_id = #{payAreaId} 
    and version = #{version} 
    and salary_project_id = #{salaryProjectId}

mapper.update:
update human_hs_social_security
salary_project_id = #{salaryProjectId}, com_pro = #{comPro}, com_mon = #{comMon}, per_pro = #{perPro}, per_mon = #{perMon}, base_ucl = #{baseUcl}, base_ll = #{baseLl}, deci_bit = #{deciBit}, carry_mode = #{carryMode}, pay_area_id = #{payAreaId}, effect_date = #{effectDate}, version = #{version}, creator = #{creator}, creator_id = #{creatorId}, create_date = #{createDate},
where id = #{id}
