mapper.select:
select id, category_id, category_name, materials_id, materials_name, storage_spaces_id, storage_spaces_name, inventory, b.today_loading_weight, c.today_unloaded_weight, create_time, clear_name from materials_mr_coal_stock a LEFT JOIN ( select material_code AS material_number,SUM(material_weight) AS today_loading_weight from t_production_configure_coal_species_performance_detail WHERE DATE_FORMAT(coal_delivery_date, '%Y-%m-%d') = DATE_FORMAT(NOW(), '%Y-%m-%d') GROUP BY material_code ) b on a.materials_id = b.material_number LEFT JOIN ( select material_code,SUM(material_weight) AS today_unloaded_weight from t_production_material_unloading_performance WHERE DATE_FORMAT(unloading_time, '%Y-%m-%d') = DATE_FORMAT(NOW(), '%Y-%m-%d') GROUP BY material_code ) c on a.materials_id = c.material_code
and category_id = #{condition.categoryId}
and materials_id = #{condition.materialsId}
and storage_spaces_id = #{condition.storageSpacesId}
and create_time >= concat(#{condition.createStartTime},' 00:00:00')
and create_time <= concat(#{condition.createEndTime},' 23:59:59')
and clear_name >= concat(#{condition.clearStartTime},' 00:00:00')
and clear_name <= concat(#{condition.clearEndTime},' 23:59:59')
order by ${condition.order} ${condition.orderby}

select a.*, b.category_id, c.category_name from t_production_configure_coal_species_performance_detail a
LEFT JOIN materials_mr_code b on a.material_code = b.materials_code
LEFT JOIN materials_mr_category c on b.category_id = c.id


        and a.material_name = #{condition.materialsName}


        and a.slot_number = #{condition.storageSpacesId}


order by ${condition.order} ${condition.orderby}

select * from t_production_material_unloading_performance

    ISNULL(unloading_time) = 0

        and material_code = #{condition.materialsId}


        and storage_maintain_id = #{condition.storageSpacesId}


order by ${condition.order} ${condition.orderby}
